
public class zuoye3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("小明左手有牌为10，右手牌为8");
		int a = 10;  //定义abcd并赋值
		int b = 8;
		int c = 0;
		
		System.out.println("现在，小明左右手互换牌！");
		c = a;
		a = b;
		b = c;
		System.out.println("于是小明左手为" + a + "右手为" + b);
		
	}

}
